import React, { useState, useEffect } from 'react';
import axiosWithAuth from '../utilities/axiosWithAuth';
import { useHistory, useParams } from 'react-router-dom';

export default function AddEvent() {
    const { push } = useHistory();
    const { id } = useParams();

  
    const [formValues, setFormValues] = useState({
        potluck_name: '',
        potluck_description:'',
        potluck_date: '',
        potluck_time: '',
        potluck_location: '',
    });
    const [itemValues, setItemValues] = useState([{potluck_item: ''}])

    useEffect(() => {
        axiosWithAuth().get(`/potlucks/${id}`)
            .then(res => {
                setFormValues(res.data);
            })
            .catch(err => {console.log({ err })
            })
    },[id])

    const handleChange = (e, i) => {
        setFormValues({
            ...formValues,
            [e.target.name]: e.target.value
        })

    }

    const onChange = (i, e) => {
      let newItemValues = [...itemValues];
      newItemValues[i][e.target.name] = e.target.value;
      setItemValues(newItemValues);
   }

   const addFormFields = () => {
     setItemValues([...itemValues], { potluck_item:''})
   }

    const handleSubmit = (e) => {
        e.preventDefault()
        axiosWithAuth().post(`/potlucks`, formValues, itemValues)
        .then(res => {
            setFormValues(res.data)
            push('/upcomingevents')
        })
        .catch(err => console.log(err))
        
    }


    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor= 'potluck_name'>Potluck Name</label>
             <input
                id='potluck_name'
                value={formValues.potluck_name}
                name='potluck_name'
                onChange={handleChange}
                />
            <label htmlFor= 'potluck_time'>Time</label>
            <label htmlFor= 'potluck_description'>Potluck Description</label>
            <textarea
                id='potluck_description'
                value={formValues.potluck_description}
                name='potluck_description'
                type='text'
                onChange={handleChange}
                rows='10'
                cols='40'
             />
             <input 
                id='time'
                value={formValues.potluck_time}
                name='potluck_time'
                type='time'
                onChange={handleChange}
                />
            <label htmlFor='potluck_date'>Date</label>
             <input 
                id='date'
                value={formValues.potluck_date}
                name='potluck_date'
                type='date'
                onChange={handleChange}
             />
            <label htmlFor='potluck_location'>Location</label> 
             <input
                id='potluck_location'
                value={formValues.potluck_location}
                name='potluck_location'
                onChange={handleChange}
             />
           {itemValues.map((element, index) => (
            <div className="form-inline" key={index}>
            <label>Item to bring</label>
              <input 
                  type="text" 
                  name="potluck_item" 
                  value={element.potluck_item || ""} 
                  onChange={e => onChange(index, e)} />
                  <button onclick={() => addFormFields}>+</button>
              
              {
                index ? 
                  <button type="button"  className="button remove" >Remove</button> 
                : null
              }
            </div>
          ))}
             <br/>
            <button>Save Changes</button>
            
        </form>
    )
}
