import React, { useState, useEffect } from 'react';
import axiosWithAuth from '../utilities/axiosWithAuth';
import { useHistory, useParams } from 'react-router-dom';

export default function AddEvent() {
    const { push } = useHistory();
    const { id } = useParams();

    const [formValues, setFormValues] = useState({
        potluck_name: '',
        potluck_description:'',
        potluck_date: '',
        potluck_time: '',
        potluck_location: '',
    });
    const [itemValues, setItemValues] = useState([{potluck_item: ''}])

    useEffect(() => {
        axiosWithAuth().get(`/potlucks/${id}`)
            .then(res => {
                setFormValues(res.data);
            })
            .catch(err => {console.log({ err })
            })
    },[id])

    const handleChange = (e) => {
        setFormValues({
            ...formValues,
            ...itemValues,
            [e.target.name]: e.target.value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        axiosWithAuth().post(`/potlucks`, formValues)
        .then(res => {
            setFormValues(res.data)
            push('/upcomingevents')
        })
        .catch(err => console.log(err))
        
    }


    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor= 'potluck_name'>Potluck Name</label>
             <input
                id='potluck_name'
                value={formValues.potluck_name}
                name='potluck_name'
                onChange={handleChange}
                />
            <label htmlFor= 'potluck_time'>Time</label>
            <label htmlFor= 'potluck_description'>Potluck Description</label>
            <textarea
                id='potluck_description'
                value={formValues.potluck_description}
                name='potluck_description'
                type='text'
                onChange={handleChange}
                rows='10'
                cols='40'
             />
             <input 
                id='time'
                value={formValues.potluck_time}
                name='potluck_time'
                type='time'
                onChange={handleChange}
                />
            <label htmlFor='potluck_date'>Date</label>
             <input 
                id='date'
                value={formValues.potluck_date}
                name='potluck_date'
                type='date'
                onChange={handleChange}
             />
            <label htmlFor='potluck_location'>Location</label> 
             <input
                id='potluck_location'
                value={formValues.potluck_location}
                name='potluck_location'
                onChange={handleChange}
             />
           {itemValues.map(item => 
           <div className='potluckItemContainer'> 
            <label htmlFor='potluck_item'>Potluck Item</label>
             <input
                id='potluck_item'
                value={item.potluck_item}
                name='potluck_item'
                onChange={handleChange}
             />
           </div>
             )}
             <br/>
            <button>Save Changes</button>
            
        </form>
    )
}
