import React, { useState, Fragment } from 'react';
import axiosWithAuth from '../utilities/axiosWithAuth';
import { useHistory } from 'react-router-dom';

export default function AddEvent() {
    const [inputFields, setInputFields] = useState([
      {  potluck_name: '', potluck_description:'', potluck_date: '',potluck_time: '', potluck_location: '', potluck_item:''}
      ]);
    
      const handleAddFields = () => {
        const values = [...inputFields];
        values.push({ potluck_item:'' });
        setInputFields(values);
      };
    
      const handleRemoveFields = index => {
        const values = [...inputFields];
        values.splice(index, 1);
        setInputFields(values);
      };
    
      const handleInputChange = (e) => {
        setInputFields({
            ...inputFields,
            [e.target.name]: e.target.value
        })
    }
    
      const handleSubmit = e => {
        e.preventDefault();
        console.log("inputFields", inputFields);
      };
    
      return (
        <>
          <h1>Dynamic Form Fields in React</h1>
          <form onSubmit={handleSubmit}>
          {inputFields.map((inputField, index) => (
                <Fragment key={`${inputField}~${index}`}>
            <label htmlFor= 'potluck_name'>Potluck Name</label>
             <input
                id='potluck_name'
                value={inputFields.potluck_name}
                name='potluck_name'
                onChange={handleInputChange}
                />
            <label htmlFor= 'potluck_time'>Time</label>
            <label htmlFor= 'potluck_description'>Potluck Description</label>
            <textarea
                id='potluck_description'
                value={inputFields.potluck_description}
                name='potluck_description'
                type='text'
                onChange={handleInputChange}
                rows='10'
                cols='40'
             />
             <input 
                id='time'
                value={inputFields.potluck_time}
                name='potluck_time'
                type='time'
                onChange={handleInputChange}
                />
            <label htmlFor='potluck_date'>Date</label>
             <input 
                id='date'
                value={inputFields.potluck_date}
                name='potluck_date'
                type='date'
                onChange={handleInputChange}
             />
            <label htmlFor='potluck_location'>Location</label> 
             <input
                id='potluck_location'
                value={inputFields.potluck_location}
                name='potluck_location'
                onChange={handleInputChange}
             />
           
            <div className="form-row">
            <label htmlFor='potluck_item'>Potluck Item</label> 
                  <input
                     id='potluck_item'
                     value={inputFields.potluck_item}
                     name='potluck_item'
                     onChange={handleInputChange}
                  />
                  <button type='button' onClick={handleAddFields}>+</button>
                 </div>
                     </Fragment>
                   ))}
                
            <div className="submit-button">
              <button
                className="btn btn-primary mr-2"
                type="submit"
                onSubmit={handleSubmit}
              >
                Save
              </button>
            </div>
          </form>
        </>
      );
    };
